import { gql, useMutation } from '@apollo/client';
import { ShirtColor } from 'graphql-global-types';
import { useCallback } from 'react';
import {AddShirtMutation} from 'generated';

type Props = {
  onDone: () => void;
};

export const ADD_SHIRT_MUTATION = gql`
  mutation AddShirt($input: AddShirtInput!) {
    addShirt(data: $input) {
      id
      name
      type
      color
      size
    }
  }
`;

export const AddShirt: React.FunctionComponent<Props> = ({ onDone }: Props) => {
  const handleSubmit = useCallback(
    (event: React.FormEvent) => {
      console.log(event);
      console.log(event.target);
      console.log(new FormData(event.target as HTMLFormElement));
      const formData = new FormData(event.target as HTMLFormElement);
      for (const v in formData) {
        console.log(v);
      }
      event.preventDefault();
      onDone();
    },
    [onDone],
  );
  const [addShirt] = useMutation<AddShirtMutation>(ADD_SHIRT_MUTATION);
  return (
    <div>
      <form onSubmit={handleSubmit}>
        <div>
          <label>
            Name:
            <input name="name" />
          </label>
          <label>
            Color:
            <select name="color" required>
              {Object.values(ShirtColor).map((color) => (
                <option key={color} value={color}>
                  {color}
                </option>
              ))}
            </select>
          </label>
          <label>
            Size:
            <input name="size" min="1" type="number" />
          </label>
        </div>
        <div>
          <button type="submit">Add</button>
          <button type="button" onClick={onDone}>
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};
