import { gql } from '@apollo/client';
import { ShirtColor } from 'graphql-global-types';
import { useCallback } from 'react';

type Props = {
  onDone: () => void;
};

export const ADD_SHIRT_MUTATION = gql`
  mutation AddShirt($input: AddShirtInput!) {
    addShirt(data: $input)
  }
`;

export const AddShirt: React.FunctionComponent<Props> = ({ onDone }: Props) => {
  const handleSubmit = useCallback(
    (event) => {
      event.preventDefault();
      onDone();
    },
    [onDone],
  );
  const [addShirt] = useMutation<RemoveProductMutation>(REMOVE_PRODUCT_MUTATION);
  const handleRemoveProduct = useCallback((id: number) => {
    removeProduct({
      variables: {
        id,
      },
    });
  }, []);
  return (
    <div>
      <form onSubmit={handleSubmit}>
        <div>
          <label>
            Name:
            <input name="name" />
          </label>
          <label>
            Color:
            <select name="color" required>
              {Object.values(ShirtColor).map((color) => (
                <option key={color} value={color}>
                  {color}
                </option>
              ))}
            </select>
          </label>
          <label>
            Size:
            <input name="size" min="1" type="number" />
          </label>
        </div>
        <div>
          <button type="submit">Add</button>
          <button type="button" onClick={onDone}>
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};
