import { Min, MinLength } from 'class-validator';
import { Arg, Field, InputType, Int, Mutation, Query, Resolver } from 'type-graphql';
import { ShirtColor } from '../db/entity/ShirtEntity';
import { Pants } from './Pants';
import { Product } from './Product';
import { Shirt } from './Shirt';

@InputType()
class AddShirtInput implements Partial<Shirt> {
  @Field()
  name: string;

  @Field(() => ShirtColor)
  color: ShirtColor;

  @Field(() => Int)
  @Min(1)
  size: number;
}

@InputType()
class AddPantsInput implements Partial<Pants> {
  @Field()
  @MinLength(1)
  color: string;

  @Field(() => Int)
  @Min(1)
  width: number;

  @Field(() => Int)
  @Min(1)
  length: number;
}

@Resolver(() => Product)
export class ProductResolver {
  @Query(() => [Product])
  async products(): Promise<Product[]> {
    return await [];
  }

  @Mutation()
  async removeProduct(@Arg('id') id: number): Promise<void> {
    console.log('Removing');
    console.log(id);
  }

  @Mutation()
  async addShirt(@Arg('data') newShirt: AddShirtInput): Promise<Shirt> {
    console.log(newShirt);
    return null;
  }

  @Mutation()
  async addPants(@Arg('data') newPants: AddPantsInput): Promise<Pants> {
    console.log(newPants);
    return null;
  }
}
