import { gql, useMutation } from '@apollo/client';
import { AddPantsInput, AddPantsMutation } from 'generated';
import { useCallback } from 'react';

type Props = {
  onDone: () => void;
};

export const ADD_PANTS_MUTATION = gql`
  mutation AddShirt($input: AddShirtInput!) {
    addShirt(data: $input) {
      id
      name
      type
      color
      size
    }
  }
`;

export const AddPants: React.FunctionComponent<Props> = ({ onDone }: Props) => {
  const [addShirt] = useMutation<AddPantsMutation>(ADD_PANTS_MUTATION);
  const handleSubmit = useCallback(
    async (event: React.FormEvent) => {
      const formData = new FormData(event.target as HTMLFormElement);
      const input: AddPantsInput = {
        color: formData.get('color').toString(),
        name: formData.get('name').toString(),
        width: parseInt(formData.get('width').toString(), 10),
        length: parseInt(formData.get('length').toString(), 10),
      };
      event.preventDefault();
      await addShirt({
        variables: {
          input,
        },
      });
      onDone();
    },
    [onDone],
  );
  return (
    <div>
      <form onSubmit={handleSubmit}>
        <div>
          <label>
            Name:
            <input name="name" />
          </label>
          <label>
            Color:
            <input name="color" required />
          </label>
          <label>
            Width:
            <input name="width" min="1" type="number" />
          </label>
          <label>
            Length:
            <input name="length" min="1" type="number" />
          </label>
        </div>
        <div>
          <button type="submit">Add</button>
          <button type="button" onClick={onDone}>
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};
