import { Product } from 'generated';
import { ItemProp } from './ItemProp';

type Props = {
  product: Product;
  onRemove: (id: number) => void;
};

export const ProductItem: React.FunctionComponent<Props> = ({ product, onRemove }: Props) => {
  return (
    <li>
      <ItemProp label="Type">{product.type}</ItemProp>
      <ItemProp label="Name">{product.name}</ItemProp>
      <button onClick={() => onRemove(product.id)}>Delete</button>
    </li>
  );
};
