import { useCallback } from 'react';

type Props = {
  product: any;
  onRemove: (id: number) => void;
};

export const ProductItem: React.FunctionComponent = ({ product, onRemove }: Props) => {
  return (
    <li>
      {product.type === 'shirt' ? 'Shirt' : 'Pants'}
      {product.name}
      <button onClick={() => onRemove(product.id)} />
    </li>
  );
};
